            new Food { FoodID = 74, FoodName = "Pear", FoodCategoryID = 7, Protein = 1, Carbonhydrate = 13, Fat = 0, Calorie = 57, Description = "Pear is a highly nutritious fruit. It is a rich source of dietary fiber, vitamins (especially vitamin C), and minerals that are important for the body's energy needs. Pear also contains antioxidants and supports the digestive system, improving gut health. In summary, pear is a valuable option for a healthy diet.", FoodPicture = "../../../images/foodimages/armut.jfif", Amount = 100 },
