            new Food { FoodID = 92, FoodName = "Lentil soup", FoodCategoryID = 8, Protein = 7.3, Carbonhydrate = 24.8, Fat = 0.5, Calorie = 136, Description = "Lentil soup is a soup whose main ingredient is lentils; it can be vegetarian or contain meat and use brown, red, yellow, green or black lentils, with or without shells. The yellow and red lentils, which have been dehulled, will be crushed as they cook, making a thick soup.", FoodPicture = "../../../images/foodimages/LentilSoup.jfif", Amount = 100 },
