            new Food { FoodID = 97, FoodName = "Dry bean Meal", FoodCategoryID = 9, Protein = 17.5, Carbonhydrate = 45.1, Fat = 0.63, Calorie = 250, Description = "Dry beans are a cooked bean dish in Turkish cuisine. It is made with white beans and olive oil and mostly uses onion and tomato paste. Preferably vegetables or meat can be added to the dish. Dried beans are served with rice pilaf or bulgur pilaf. It can be consumed by adding bread to its water.", FoodPicture = "../../../images/foodimages/DryBeanmeal.jfif", Amount = 250 },
            new Food { FoodID = 98, FoodName = "Chicken Doner", FoodCategoryID = 9, Protein = 20.6, Carbonhydrate = 45.1, Fat = 19.6, Calorie = 148.8, Description = "Chicken doner kebab is made from chicken meat. Chicken doner, which finds its place in Turkish cuisine, is one of the most frequently consumed fast-food products in Turkey.", FoodPicture = "../../../images/foodimages/ChickenDoner.jfif", Amount = 120 },
