            new Food { FoodID = 100, FoodName = "Karniyarık Meal", FoodCategoryID = 9, Protein = 11.1, Carbonhydrate = 8.7, Fat = 12.2, Calorie = 191, Description = "Karnıyarık is one of the main eggplant dishes of Turkish cuisine. In addition to eggplant, which is the main ingredient, onions, peppers, tomatoes and minced meat are also used during preparation. A similar eggplant dish is imambayildi. The difference of Imambayildi from Karnıyarık is that there is no meat in it and it is served cold.", FoodPicture = "../../../images/foodimages/KarniyarıkMeal.jfif", Amount = 250 },
