            new Food { FoodID = 102, FoodName = "Chicken Saute", FoodCategoryID = 9, Protein = 42, Carbonhydrate = 11, Fat = 22, Calorie = 423, Description = "Cooking meat and vegetables in a pan at high heat is called saute. Sauteing, which is a cooking method, is prepared by cooking meat and vegetables in a short time by stirring constantly. It is very important that the materials to be used while sautéing are chopped into small pieces in the form of cubes.", FoodPicture = "../../../images/foodimages/ChickenSaute.jfif", Amount = 240 },
            new Food { FoodID = 103, FoodName = "Boiled Veal", FoodCategoryID = 9, Protein = 12, Carbonhydrate = 1.7, Fat = 3.5, Calorie = 88, Description = "Nutritional values such as the taste of the dish made from beef are also important for the human body.", FoodPicture = "../../../images/foodimages/BoiledVeal.jfif", Amount = 100 },
