            new Food { FoodID = 105, FoodName = "Pasta", FoodCategoryID = 10, Protein = 5.8, Carbonhydrate = 30.5, Fat = 0.3, Calorie = 157, Description = "Pasta is also very rich in vitamins A, B1, B2, iron, calcium, phosphorus, potassium and protein. It is oil free. It has a cholesterol-lowering effect as it contains very little (1-1,5%) minor fat components and a high rate of linoleic fatty acid.Complex carbohydrates in pasta keep you full because they mix slowly into the blood.", FoodPicture = "../../../images/foodimages/Pasta.jfif", Amount = 100 },
            new Food { FoodID = 106, FoodName = "Bulgur Rice", FoodCategoryID = 10, Protein = 2.7, Carbonhydrate = 18.1, Fat = 3.3, Calorie = 114, Description = "Bulgur, which contains vitamin B1, magnesium and folate, plays an important role for the nervous system and digestive system. Because bulgur contains high levels of minerals and vitamins, it ensures regular and healthy functioning of the intestines. Bulgur, which prevents constipation, also relaxes the mind and provides high energy.", FoodPicture = "../../../images/foodimages/BulgurRice.jfif", Amount = 100 },
            new Food { FoodID = 107, FoodName = "Rice", FoodCategoryID = 10, Protein = 2, Carbonhydrate = 24.7, Fat = 5.8, Calorie = 162, Description = "All rice varieties are a great source of minerals such as calcium and iron; It is also rich in vitamins such as niacin, vitamin D, thiamine, fiber and riboflavin. Rice is easy to digest and has less saturated fat and good cholesterol compared to other foods. Therefore, it is good for heart health.", FoodPicture = "../../../images/foodimages/Rice.jfif", Amount = 100 },

            new Food { FoodID = 108, FoodName = "Seasonal Salad", FoodCategoryID = 11, Protein = 1.3, Carbonhydrate = 3.8, Fat = 1, Calorie = 31, Description = "Although each type of leafy vegetable has different health benefits, they are all low-calorie and high-fiber foods. This means you will fill your stomach with fewer calories. Eating foods that contain fiber can also improve the health of the digestive system. Black lettuce, kale and spinach are rich in vitamins A, C, E and K. Arugula and mustard vegetables also provide most of the B vitamins.", FoodPicture = "../../../images/foodimages/SeasonalSalad.jfif", Amount = 100 },
